SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_se_todos_itens_aud ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(1) := 'S';
nr_seq_analise_conta_item_w	bigint;
ie_analise_nova_w			varchar(1);
cd_estabelecimento_w		estabelecimento.cd_estabelecimento%type;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_analise_conta_item a
	where	a.nr_seq_analise = nr_seq_analise_p
	and	a.ie_status = 'P'
	and	a.ie_tipo = 'O'
	order by 1;

--OS916358 - Em conversa com o Diogo, foi decidido que na análise antiga a crítica de
--itens pendentes também precisa ser feita no caso de ter glosas pendentes.
C02 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_analise_conta_item a
	where	a.nr_seq_analise = nr_seq_analise_p
	and	a.ie_status = 'P'
	order by 1;



BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select	max(a.ie_analise_cm_nova) ie_analise_cm_nova
into STRICT	ie_analise_nova_w
from	pls_parametros a
where	a.cd_estabelecimento	= cd_estabelecimento_w;

--Análise antiga
if ((ie_analise_nova_w IS NOT NULL AND ie_analise_nova_w::text <> '') or ie_analise_nova_w = 'N') then

	open C02;
	loop
	fetch C02 into
		nr_seq_analise_conta_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		ds_retorno_w := 'N';

		end;
	end loop;
	close C02;

else
	open C01;
	loop
	fetch C01 into
		nr_seq_analise_conta_item_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		ds_retorno_w := 'N';

		end;
	end loop;
	close C01;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
