CREATE OR REPLACE FUNCTION pls_obter_se_todos_itens_cor ( nr_seq_analise_p bigint, nr_seq_grupo_atual_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'S';
nr_seq_analise_conta_item_w	bigint;

/*Verificar se existe ocorrências que necessitam de correção.*/

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_analise_conta_item a
	where	a.nr_seq_analise = nr_seq_analise_p
	and	a.ie_status = 'E'
	and	a.ie_tipo = 'O'
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_analise_conta_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	ds_retorno_w := 'N';

	end;
end loop;
close C01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
