CREATE OR REPLACE FUNCTION pls_obter_se_tratamento_benef ( dt_requisicao_p timestamp, nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(1) := 'N';
qt_reg_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	pls_tratamento_benef
where	nr_seq_segurado = nr_seq_segurado_p
and	dt_requisicao_p between trunc(dt_inicio_tratamento) and  fim_dia(dt_fim_tratamento)
and	ie_estagio_tratamento = 1;

if (qt_reg_w > 0) then
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
