CREATE OR REPLACE FUNCTION pls_obter_se_urgencia_emergen (nr_seq_conta_p pls_conta_proc.nr_seq_conta%type, nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_retorno_w			varchar(1);
ie_carater_internacao_w		pls_conta.ie_carater_internacao%type;
qt_item_w			integer	:= 0;
cd_guia_referencia_w		pls_conta.cd_guia_ok%type;
nr_seq_segurado_w		pls_segurado.nr_sequencia%type;
/*Irá retornar se o procedimento recebeu adicional de horário e foi executado em regime de urgência/emergência necessário para o PTU 5.0*/

BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '')then
	select	max(ie_carater_internacao),
		max(cd_guia_ok),
		max(nr_seq_segurado)
	into STRICT	ie_carater_internacao_w,
		cd_guia_referencia_w,
		nr_seq_segurado_w
	from	pls_conta
	where	nr_sequencia	= nr_seq_conta_p;

	if (coalesce(ie_carater_internacao_w::text, '') = '') then
		select	max(ie_carater_internacao)
		into STRICT	ie_carater_internacao_w
		from	pls_conta
		where	cd_guia_ok	= cd_guia_referencia_w
		and	nr_seq_segurado	= nr_seq_segurado_w
		and	ie_tipo_guia	= '5';
	end if;
	if (ie_carater_internacao_w	= 'U') then
		if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then
			select	count(1)
			into STRICT	qt_item_w
			from	pls_conta_proc
			where	nr_sequencia		= nr_seq_conta_proc_p
			and	(nr_seq_regra_horario IS NOT NULL AND nr_seq_regra_horario::text <> '');
		end if;

		if (qt_item_w > 0) then
			ie_retorno_w := 'S';
		else
			ie_retorno_w := 'N';
		end if;
	else
		ie_retorno_w	:= 'N';
	end if;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
