CREATE OR REPLACE FUNCTION pls_obter_se_usuario_master ( nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_usuario_master_w	varchar(1);


BEGIN

select	CASE WHEN count(a.nr_sequencia)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_usuario_master_w
from	pls_membro_grupo_aud a,
	pls_grupo_auditor b
where	a.nm_usuario_exec = nm_usuario_p
and	coalesce(b.ie_permissao,'X') in ('T','R')  -- tratamento para acesso total com restrições Demitrius 07/02/2011
and	a.nr_seq_grupo = b.nr_sequencia
and	a.ie_situacao = 'A'
and	b.ie_situacao = 'A';

return	ie_usuario_master_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
