CREATE OR REPLACE FUNCTION pls_obter_se_valor_calculado ( nr_seq_conta_p pls_conta.nr_sequencia%type, nr_seq_guia_p pls_guia_plano.nr_sequencia%type, vl_minimo_p bigint, nr_seq_requisicao_p pls_requisicao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


vl_calculado_proc_w		double precision;
vl_calculado_mat_w		double precision;
ds_retorno_w			varchar(1) := 'N';


BEGIN

if (nr_seq_conta_p IS NOT NULL AND nr_seq_conta_p::text <> '') then
	select	sum(vl_procedimento)
	into STRICT	vl_calculado_proc_w
	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_p;

	select	sum(vl_material)
	into STRICT	vl_calculado_mat_w
	from	pls_conta_mat
	where	nr_seq_conta = nr_seq_conta_p;

	if	((coalesce(vl_calculado_proc_w,0) + coalesce(vl_calculado_mat_w,0)) > vl_minimo_p) then
		ds_retorno_w := 'S';
	end if;
elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	select	sum(vl_procedimento)
	into STRICT	vl_calculado_proc_w
	from	pls_guia_plano_proc
	where	nr_seq_guia = nr_seq_guia_p;

	select	sum(vl_material)
	into STRICT	vl_calculado_mat_w
	from	pls_guia_plano_mat
	where	nr_seq_guia = nr_seq_guia_p;

	if	((coalesce(vl_calculado_proc_w,0) + coalesce(vl_calculado_mat_w,0)) > vl_minimo_p) then
		ds_retorno_w := 'S';
	end if;
elsif (nr_seq_requisicao_p IS NOT NULL AND nr_seq_requisicao_p::text <> '') then
	select	sum(vl_procedimento)
	into STRICT	vl_calculado_proc_w
	from	pls_requisicao_proc
	where	nr_seq_requisicao = nr_seq_requisicao_p;

	select	sum(vl_material)
	into STRICT	vl_calculado_mat_w
	from	pls_requisicao_mat
	where	nr_seq_requisicao = nr_seq_requisicao_p;

	if	((coalesce(vl_calculado_proc_w,0) + coalesce(vl_calculado_mat_w,0)) > vl_minimo_p) then
		ds_retorno_w := 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
