CREATE OR REPLACE FUNCTION pls_obter_se_vendedor_escala ( nr_seq_regra_escala_p bigint, nr_seq_origem_solic_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(10);
qt_registros_w		bigint;


BEGIN

ds_retorno_w	:= 'N';

if (coalesce(nr_seq_origem_solic_p::text, '') = '') then
	return 'S';
end if;

select	count(1)
into STRICT	qt_registros_w
from	PLS_SOLIC_REGRA_ESCALA		b,
	PLS_SOLICITACAO_REGRA_ATEN	a
where	b.nr_seq_regra			= a.nr_sequencia
and	a.nr_sequencia			= nr_seq_regra_escala_p;

if (qt_registros_w = 0) then
	ds_retorno_w	:= 'S';
else
	select	count(1)
	into STRICT	qt_registros_w
	from	PLS_SOLIC_REGRA_ESCALA		b,
		PLS_SOLICITACAO_REGRA_ATEN	a
	where	b.nr_seq_regra			= a.nr_sequencia
	and	a.nr_sequencia			= nr_seq_regra_escala_p
	and	b.NR_SEQ_ORIGEM_SOLIC		= nr_seq_origem_solic_p;

	if (qt_registros_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
