CREATE OR REPLACE FUNCTION pls_obter_se_vendedor_pf_pj ( nr_seq_vendedor_canal_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w	varchar(2);


BEGIN

select	substr( CASE WHEN  coalesce(cd_cgc::text, '') = '' THEN  'PF'  ELSE 'PJ' END , 1,2)
into STRICT	ie_retorno_w
from	pls_vendedor
where	nr_sequencia	= nr_seq_vendedor_canal_p;

return	ie_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
