CREATE OR REPLACE FUNCTION pls_obter_se_vinculado ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


qt_vinculado_w		integer	:= 0;
ie_vinculado_w		varchar(1)	:= 'N';


BEGIN

select	count(*)
into STRICT	qt_vinculado_w
from	pls_prestador_medico
where	cd_medico		= cd_pessoa_fisica_p
and	ie_tipo_vinculo		= 'V'
and	ie_situacao		= 'A'
and	trunc(clock_timestamp(),'dd') between trunc(coalesce(dt_inclusao,clock_timestamp()),'dd') and  fim_dia(coalesce(dt_exclusao,clock_timestamp()));

if (qt_vinculado_w	= 0) then
	select	count(*)
	into STRICT	qt_vinculado_w
	from	pls_prestador
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p
	and	ie_tipo_vinculo		= 'V';
end if;

if (qt_vinculado_w	> 0) then
	ie_vinculado_w	:= 'S';
end if;

return	ie_vinculado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
