CREATE OR REPLACE FUNCTION pls_obter_simbolos_sca_benef ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_simbolo_w		varchar(100);
nr_seq_plano_w		bigint;

C01 CURSOR FOR
	SELECT	nr_seq_plano
	from	pls_sca_vinculo
	where	nr_seq_segurado	= nr_seq_segurado_p
	and	coalesce(dt_fim_vigencia::text, '') = '';


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_plano_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	CD_PLANO
	into STRICT	ds_simbolo_w
	from	pls_plano
	where	nr_sequencia	= nr_seq_plano_w;

	ds_retorno_w	:= ds_retorno_w||ds_simbolo_w||';';

	end;
end loop;
close C01;


ds_retorno_w	:= substr(ds_retorno_w,1,length(ds_retorno_w)-1);

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
