CREATE OR REPLACE FUNCTION pls_obter_situacao_material (cd_material_p material.cd_material%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := null;


BEGIN
select	max(a.cd_material)
into STRICT	ds_retorno_w
from	material a
where	a.cd_material = cd_material_p
and	a.ie_situacao = 'A';

if (coalesce(ds_retorno_w::text, '') = '') then
	select	max(x.nr_sequencia)
	into STRICT	ds_retorno_w
	from	pls_material x
	where	x.cd_material		= cd_material_p
	and (x.dt_fim_vigencia_ref	> clock_timestamp());
end if;

if (coalesce(ds_retorno_w::text, '') = '') then
	select	max(y.nr_sequencia)
	into STRICT	ds_retorno_w
	from	material_estab y
	where	y.cd_material		= cd_material_p
	and	y.ie_farmacia_com	= 'S';
end if;

if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
	ds_retorno_w := 'A';
else
	ds_retorno_w := 'I';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
