SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_situac_auditoria_web ( nr_seq_auditoria_p bigint, nr_seq_contrato_p bigint, nr_seq_contrato_int_p bigint) RETURNS varchar AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Obter a situação da auditoria
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/grupo_analise_w			bigint;
ds_retorno_w			varchar(10);
nr_auditoria_w			bigint := null;



BEGIN

select  pls_obter_grupo_analise_atual(nr_sequencia)
into STRICT    grupo_analise_w
from    pls_auditoria
where	nr_sequencia = nr_seq_auditoria_p;

if (coalesce(grupo_analise_w::text, '') = '' or grupo_analise_w = 0) then
	ds_retorno_w := 'Finalizado';
else
	select  count(1)
	into STRICT	nr_auditoria_w
	from    pls_auditoria_grupo a,
		pls_grupo_auditor b,
		pls_membro_grupo_aud c
	where   a.nr_sequencia = grupo_analise_w
	and     b.nr_sequencia =  a.nr_seq_grupo
	and 	c.nr_seq_grupo =  a.nr_seq_grupo
	and	b.ie_tipo_auditoria = 3
	and (c.nr_seq_contrato = coalesce(nr_seq_contrato_p,0)
	or	 c.nr_seq_intercambio = coalesce(nr_seq_contrato_int_p,0));

	if (nr_auditoria_w > 0) then
		ds_retorno_w := 'Pendente';
	else
		ds_retorno_w := 'Andamento';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_situac_auditoria_web ( nr_seq_auditoria_p bigint, nr_seq_contrato_p bigint, nr_seq_contrato_int_p bigint) FROM PUBLIC;

