CREATE OR REPLACE FUNCTION pls_obter_situac_contr_periodo ( nr_seq_contrato_p bigint, ie_tipo_contrato_p text, ie_tipo_periodo_p text, dt_periodo_p timestamp) RETURNS varchar AS $body$
DECLARE


/*
ie_tipo_contrato_p
I - Intercâmbio
C - Operadora
*/
/*
ie_tipo_periodo_p
D - Dia
M - Mês
*/
ie_retorno_w			varchar(10);
dt_rescisao_contr_w		timestamp;
dt_periodo_w			timestamp;


BEGIN

if (ie_tipo_contrato_p = 'C') then
	select	coalesce(dt_rescisao_contrato,dt_cancelamento)
	into STRICT	dt_rescisao_contr_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_p;
elsif (ie_tipo_contrato_p = 'I') then
	select	dt_exclusao
	into STRICT	dt_rescisao_contr_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_contrato_p;
end if;

if (coalesce(dt_rescisao_contr_w::text, '') = '') then
	return 'A';
else
	if (ie_tipo_periodo_p	= 'D') then
		dt_rescisao_contr_w	:= trunc(dt_rescisao_contr_w,'dd');
		dt_periodo_w		:= trunc(dt_periodo_p,'dd');
	elsif (ie_tipo_periodo_p	= 'M') then
		dt_rescisao_contr_w	:= trunc(dt_rescisao_contr_w,'Month');
		dt_periodo_w		:= trunc(dt_periodo_p,'Month');
	end if;

	if (dt_rescisao_contr_w	<= dt_periodo_w) then
		return 'N';
	else
		return 'A';
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
