SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_sit_glosa (nr_seq_referencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
	C = Conta
	S = Servi√ßo
	M = Material */
ds_retorno_w		varchar(255);
ie_status_w		varchar(1);
ie_glosa_w		varchar(1);
vl_glosa_w		double precision;


BEGIN
if (ie_opcao_p = 'C') then
	select	max(a.ie_status),
		coalesce(max(a.vl_glosa),0),
		max(a.ie_glosa)
	into STRICT	ie_status_w,
		vl_glosa_w,
		ie_glosa_w
	from	pls_conta	a
	where	a.nr_sequencia	= nr_seq_referencia_p;
elsif (ie_opcao_p = 'S') then
	select	max(a.ie_status),
		coalesce(max(a.vl_glosa),0),
		max(a.ie_glosa)
	into STRICT	ie_status_w,
		vl_glosa_w,
		ie_glosa_w
	from	pls_conta_proc	a
	where	a.nr_sequencia	= nr_seq_referencia_p;
elsif (ie_opcao_p = 'M') then
	select	max(a.ie_status),
		coalesce(max(a.vl_glosa),0),
		max(a.ie_glosa)
	into STRICT	ie_status_w,
		vl_glosa_w,
		ie_glosa_w
	from	pls_conta_mat	a
	where	a.nr_sequencia	= nr_seq_referencia_p;
end if;

if (ie_opcao_p in ('L','S')) then
	if (vl_glosa_w = 0) then
		ds_retorno_w	:= 'Liberado sem glosa';
	else
		if (ie_glosa_w = 'S') then
			ds_retorno_w	:= 'Glosado totalmente';
		else
			ds_retorno_w	:= 'Glosado parcialmente';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
