CREATE OR REPLACE FUNCTION pls_obter_soma_vl_itens_pacote (nr_seq_preco_pacote_p bigint, ie_tipo_guia_p bigint, nr_seq_prestador_p bigint, ie_tipo_item_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
/*
	ie_tipo_item_p
		1 = Diária
		2 = Taxa
		3 = Medicamento
		4 = Material
		5 = OPME
*/
ds_retorno_w		varchar(255);


BEGIN

if (ie_tipo_item_p	= 1) then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_procedimento_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	procedimento a,
		pls_pacote_procedimento b
	where	b.ie_situacao	= 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced
	and	a.cd_tipo_procedimento	= 37;

elsif (ie_tipo_item_p	= 2) then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_procedimento_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	procedimento a,
		pls_pacote_procedimento b
	where	b.ie_situacao	= 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and	a.cd_procedimento	= b.cd_procedimento
	and	a.ie_origem_proced	= b.ie_origem_proced
	and	a.cd_tipo_procedimento	= 38;

elsif (ie_tipo_item_p	= 3) then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_material_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	pls_material a,
		pls_pacote_material b
	where	b.ie_situacao	 = 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and	a.nr_sequencia		= b.nr_seq_material
	and	a.ie_tipo_despesa	= 2;

elsif (ie_tipo_item_p	= 4) then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_material_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	pls_material a,
		pls_pacote_material b
	where	b.ie_situacao	 = 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and	a.nr_sequencia		= b.nr_seq_material
	and	a.ie_tipo_despesa	= 3;

elsif (ie_tipo_item_p	= 5) then
	select	to_char(sum(coalesce(b.vl_negociado, b.vl_material_regra)), 'FM999G999G999D90')
	into STRICT	ds_retorno_w
	from	pls_material a,
		pls_pacote_material b
	where	b.ie_situacao	 = 'A'
	and 	b.nr_seq_composicao = (	SELECT	max(c.nr_seq_composicao)
					from	pls_pacote_tipo_acomodacao c
					where	c.nr_sequencia = nr_seq_preco_pacote_p)
	and	a.nr_sequencia		= b.nr_seq_material
	and	a.ie_tipo_despesa	= 7;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
