CREATE OR REPLACE FUNCTION pls_obter_status_alt_produto (nr_seq_solic_alt_p bigint) RETURNS varchar AS $body$
DECLARE


dt_rejeicao_w			pls_segurado_solic_alt.dt_rejeicao%type;
dt_confirmacao_alteracao_w	pls_segurado_solic_alt.dt_confirmacao_alteracao%type;
ie_status_w			varchar(10);



BEGIN

if (nr_seq_solic_alt_p IS NOT NULL AND nr_seq_solic_alt_p::text <> '') then
	select  dt_rejeicao,
		dt_confirmacao_alteracao
	into STRICT	dt_rejeicao_w,
		dt_confirmacao_alteracao_w
	from    pls_segurado_solic_alt
	where   (nr_seq_alt_plano IS NOT NULL AND nr_seq_alt_plano::text <> '')
	and	nr_sequencia = nr_seq_solic_alt_p;

	if (coalesce(dt_confirmacao_alteracao_w::text, '') = '' and coalesce(dt_rejeicao_w::text, '') = '') then
		ie_status_w := 'P';
	elsif ((dt_confirmacao_alteracao_w IS NOT NULL AND dt_confirmacao_alteracao_w::text <> '') and coalesce(dt_rejeicao_w::text, '') = '') then
		ie_status_w := 'A';
	elsif (coalesce(dt_confirmacao_alteracao_w::text, '') = '' and (dt_rejeicao_w IS NOT NULL AND dt_rejeicao_w::text <> '')) then
		ie_status_w := 'R';
	end if;
end if;
return ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
