SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_comercial ( nr_seq_solicitacao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_solicitacao_w		bigint;
nr_seq_cliente_w		bigint;
nr_seq_proposta_w		bigint;
ie_status_proposta_w		varchar(1);
is_status_comercial_w		varchar(5);
ie_status_cliente_w		varchar(2);
ie_etapa_solicitacao_w		varchar(2);


BEGIN

select	max(a.nr_sequencia),
	max(b.nr_sequencia),
	max(c.nr_sequencia)
into STRICT	nr_seq_solicitacao_w,
	nr_seq_cliente_w,
	nr_seq_proposta_w
FROM pls_solicitacao_comercial a
LEFT OUTER JOIN pls_comercial_cliente b ON (a.nr_sequencia = b.nr_seq_solicitacao)
LEFT OUTER JOIN pls_proposta_adesao c ON (b.nr_sequencia = c.nr_seq_cliente)
WHERE a.nr_sequencia	= nr_seq_solicitacao_p;

if (coalesce(nr_seq_proposta_w,0)	<> 0) then
	/* Domínio 2331 */

	select	ie_status
	into STRICT	ie_status_proposta_w
	from	pls_proposta_adesao
	where	nr_sequencia	= nr_seq_proposta_w;

	/* Domínio 3147 */

	if (ie_status_proposta_w	= 'T') then /* Contratado */
		is_status_comercial_w	:= '7'; /* Contratado */
	elsif (ie_status_proposta_w	= 'R') then /* Reprovado */
		is_status_comercial_w	:= '6'; /* Proposta de adesão reprovada */
	else
		is_status_comercial_w	:= '5'; /* Proposta de adesão pendente */
	end if;
elsif (coalesce(nr_seq_cliente_w,0)	<> 0) then
	/* Domínio 2664 */

	select	ie_status
	into STRICT	ie_status_cliente_w
	from	pls_comercial_cliente
	where	nr_sequencia	= nr_seq_cliente_w;

	/* Domínio 3147 */

	if (ie_status_cliente_w	= 'N') then
		is_status_comercial_w	:= '4'; /* Prospecção reprovada  */
	else
		is_status_comercial_w	:= '3'; /* Prospecção pendente */
	end if;
elsif (coalesce(nr_seq_solicitacao_w,0)	<> 0) then
	/* Domínio 2557 */

	select	ie_etapa_solicitacao
	into STRICT	ie_etapa_solicitacao_w
	from	pls_solicitacao_comercial
	where	nr_sequencia	= nr_seq_solicitacao_w;

	/* Domínio 3147 */

	if (ie_etapa_solicitacao_w	= 'E') then
		is_status_comercial_w	:= '2'; /* Solicitação de lead reprovada */
	else
		is_status_comercial_w	:= '1'; /* Solicitação de lead pendente */
	end if;
end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= is_status_comercial_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= substr(obter_valor_dominio(3147,is_status_comercial_w),1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
