SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_guia (nr_seq_guia_p bigint) RETURNS bigint AS $body$
DECLARE


/*	ie_estagio_retorno_w - Domínio 2055
	1 - Em auditoria
	2 - Consistido com glosa
	3 - Consisitido sem glosa
	4 - Negado
	5 - Autorizado com glosa
	6 - Autorizado sem glosa
	7 - Usuário (aguardando ação)
*/
/*	ie_situacao_w - Domínio 1832
	C - Consistida
	N - Negado pelo sistema
	U - Usuário (aguardando ação)
	A - Auditoria médica
	L - Liberado pelo usuário
	S - Liberado pelo sistema
	P - Liberado pelo auditor
	M - Negado pelo auditor
*/
/*	ie_status_guia_w - Domínio 1747
	1 - Autorizado
	2 - Em análise
	3 - Negado
*/
qt_auditoria_w			bigint := 0;
qt_negado_w				bigint := 0;
qt_lib_sistema_w		bigint := 0;
qt_lib_usuario_w		bigint := 0;
qt_sem_acao_w			bigint := 0;
ie_status_w				varchar(2);
ie_status_guia_w		varchar(2);
ie_situacao_guia_w		varchar(2);
nr_seq_guia_mat_w		bigint;
nr_seq_guia_proc_w		bigint;
ie_estagio_retorno_w	smallint;
qt_glosa_guia_w			bigint	:= 0;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status
	from	pls_guia_plano_proc
	where	nr_seq_guia	= nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = '';

c02 CURSOR FOR
	SELECT	nr_sequencia,
		ie_status
	from	pls_guia_plano_mat
	where	nr_seq_guia	= nr_seq_guia_p
	and	coalesce(nr_seq_motivo_exc::text, '') = '';


BEGIN

ie_estagio_retorno_w	:= 0;

if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then

	select	ie_status,
		ie_situacao
	into STRICT	ie_status_guia_w,
		ie_situacao_guia_w
	from	pls_guia_plano
	where	nr_sequencia	= nr_seq_guia_p;

	select	count(*)
	into STRICT	qt_glosa_guia_w
	from	pls_guia_glosa
	where	nr_seq_guia = nr_seq_guia_p;

	open c01;
	loop
	fetch c01 into	nr_seq_guia_proc_w,
			ie_status_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

		if (ie_status_w	= 'L') then
			qt_lib_usuario_w	:= qt_lib_usuario_w + 1;
		elsif (ie_status_w	= 'S') then
			qt_lib_sistema_w	:= qt_lib_sistema_w + 1;
		elsif (ie_status_w	= 'A') then
			qt_auditoria_w	:= qt_auditoria_w + 1;
		elsif (ie_status_w	= 'N') then
			qt_negado_w	:= qt_negado_w + 1;
		else
			qt_sem_acao_w	:= qt_sem_acao_w + 1;
		end if;
	end loop;
	close c01;

	open c02;
	loop
	fetch c02 into	nr_seq_guia_mat_w,
			ie_status_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */

		if (ie_status_w	= 'L') then
			qt_lib_usuario_w	:= qt_lib_usuario_w + 1;
		elsif (ie_status_w	= 'S') then
			qt_lib_sistema_w	:= qt_lib_sistema_w + 1;
		elsif (ie_status_w	= 'A') then
			qt_auditoria_w	:= qt_auditoria_w + 1;
		elsif (ie_status_w	= 'N') then
			qt_negado_w	:= qt_negado_w + 1;
		else
			qt_sem_acao_w	:= qt_sem_acao_w + 1;
		end if;
	end loop;
	close c02;

	if (ie_situacao_guia_w = 'U') then

		ie_estagio_retorno_w	:= 7;

	elsif (ie_status_guia_w = '3') then

		ie_estagio_retorno_w	:= 4;

	elsif (ie_status_guia_w = '2') then
		if (qt_negado_w	> 0) or (qt_glosa_guia_w > 0)  then
			ie_estagio_retorno_w	:= 2;
		elsif (qt_auditoria_w	> 0) then
			ie_estagio_retorno_w	:= 1;
		elsif (qt_lib_usuario_w = 0) then
			ie_estagio_retorno_w	:= 3;
		else
			ie_estagio_retorno_w	:= 2;
		end if;

	elsif (ie_status_guia_w = '1') then

		if (qt_lib_usuario_w > 0) then
			ie_estagio_retorno_w	:= 5;
		else
			ie_estagio_retorno_w	:= 6;
		end if;
	end if;
end if;

return ie_estagio_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_status_guia (nr_seq_guia_p bigint) FROM PUBLIC;

