SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_imp_a550 ( nr_seq_camara_contest_p ptu_camara_contestacao.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := 'Arquivo de contestação importado com sucesso.';
nr_seq_log_w		ptu_processo_camara_cont.nr_sequencia%type;
ie_status_processo_w	ptu_processo_camara_cont.ie_status_processo%type;


BEGIN
select	max(nr_sequencia)
into STRICT	nr_seq_log_w
from	ptu_processo_camara_cont
where	nr_seq_camara_contest	 = nr_seq_camara_contest_p
and	ie_tipo_processo = 'IA';

if (nr_seq_log_w IS NOT NULL AND nr_seq_log_w::text <> '') then
	select	coalesce(max(ie_status_processo),'FI')
	into STRICT	ie_status_processo_w
	from	ptu_processo_camara_cont
	where	nr_sequencia	= nr_seq_log_w;
end if;

if (ie_status_processo_w = 'FI') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_log_w
	from	ptu_processo_camara_cont
	where	nr_seq_camara_contest	 = nr_seq_camara_contest_p
	and	ie_tipo_processo = 'GD';

	if (nr_seq_log_w IS NOT NULL AND nr_seq_log_w::text <> '') then
		select	coalesce(max(ie_status_processo),'FI')
		into STRICT	ie_status_processo_w
		from	ptu_processo_camara_cont
		where	nr_sequencia	= nr_seq_log_w;

		if (ie_status_processo_w = 'FA') then
			ds_retorno_w := 'Falha na geração do lote de discussão.';
		end if;
	end if;

elsif (ie_status_processo_w = 'FA') then
	ds_retorno_w := 'Falha na importação do arquivo A550.';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
