CREATE OR REPLACE FUNCTION pls_obter_status_inconsist (cd_inconsistencia_p ptu_inconsistencia.cd_inconsistencia%type) RETURNS varchar AS $body$
DECLARE


ie_situacao_w		ptu_inconsistencia.ie_situacao%type;


BEGIN

select	coalesce(max(ie_situacao), 'I')
into STRICT	ie_situacao_w
from	ptu_inconsistencia
where	cd_inconsistencia	= cd_inconsistencia_p
and	ie_situacao		= 'A';

return	ie_situacao_w;

end;
$body$
