SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_item_inf ( nr_seq_item_ref_p bigint, nr_seq_analise_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
qt_itens_w		bigint;
qt_itens_glosados_w	bigint;
qt_itens_parc_w		bigint;


BEGIN
select	count(nr_sequencia)
into STRICT	qt_itens_w
from	w_pls_resumo_conta
where	nr_seq_item_ref = nr_seq_item_ref_p
and	nr_seq_analise  = nr_seq_analise_p;

select	count(nr_sequencia)
into STRICT	qt_itens_glosados_w
from	w_pls_resumo_conta
where	nr_seq_item_ref = nr_seq_item_ref_p
and	nr_seq_analise  = nr_seq_analise_p
and	ie_pagamento	= 'G';

select	count(nr_sequencia)
into STRICT	qt_itens_parc_w
from	w_pls_resumo_conta
where	nr_seq_item_ref = nr_seq_item_ref_p
and	nr_seq_analise  = nr_seq_analise_p
and	ie_pagamento	= 'P';

if (qt_itens_glosados_w = 0) and (qt_itens_parc_w = 0) then
	ds_retorno_w := 'L';
elsif (qt_itens_glosados_w = qt_itens_w) then
	ds_retorno_w := 'G';
else
	ds_retorno_w := 'P';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
