SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_item_web ( nr_seq_item_p bigint, nr_seq_req_p bigint, nr_seq_guia_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_status_w		varchar(255);
ie_tipo_processo_w	varchar(1);


BEGIN

if (nr_seq_req_p IS NOT NULL AND nr_seq_req_p::text <> '') then
	select	ie_tipo_processo
	into STRICT	ie_tipo_processo_w
	from	pls_requisicao
	where	nr_sequencia = nr_seq_req_p;

	if (ie_tipo_item_p = 'P') then
		select	obter_valor_dominio(3453,ie_status)
		into STRICT	ds_status_w
		from	pls_requisicao_proc
		where	nr_sequencia	= nr_seq_item_p;
	elsif (ie_tipo_item_p = 'M') then
		select	obter_valor_dominio(3453,ie_status)
		into STRICT	ds_status_w
		from	pls_requisicao_mat
		where	nr_sequencia	= nr_seq_item_p;
	end if;

	if (ie_tipo_processo_w = 'I') then
		ds_status_w := 'Aguardando envio';
	end if;

elsif (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	if (ie_tipo_item_p = 'P') then
		select	obter_valor_dominio(3453,ie_status)
		into STRICT	ds_status_w
		from	pls_guia_plano_proc
		where	nr_sequencia	= nr_seq_item_p;
	elsif (ie_tipo_item_p = 'M') then
		select	obter_valor_dominio(3453,ie_status)
		into STRICT	ds_status_w
		from	pls_guia_plano_mat
		where	nr_sequencia	= nr_seq_item_p;
	end if;
end if;

ds_retorno_w	:= ds_status_w;

return	ds_retorno_w;

end 	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
