SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_lote_analise ( nr_seq_lote_p bigint) RETURNS varchar AS $body$
DECLARE


ds_status_w		varchar(255);

BEGIN

if (coalesce(nr_seq_lote_p,0) > 0) then
	begin
		select	substr(obter_valor_dominio(4892,IE_STATUS),1,255)
		into STRICT	ds_status_w
		from	pls_lote_protocolo_conta
		where	nr_sequencia 	= nr_seq_lote_p;
	exception
	when others then
		ds_status_w	:= null;
	end;
end if;

return ds_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
