SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_lote_franq ( nr_seq_franq_p pls_franq_pag.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Apresentar o status do lote de franquia na função OPS  - Controle de pagamentos de franquia.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ x] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);
dt_gera_lote_w		timestamp;
dt_lib_lote_w		timestamp;
dt_fecha_lote_w		timestamp;

BEGIN
ds_retorno_w	:= 'Provisório';

select	max(dt_geracao_lote),
	max(dt_liberacao_lote),
	max(dt_fechamento_lote)
into STRICT	dt_gera_lote_w,
	dt_lib_lote_w,
	dt_fecha_lote_w
from	pls_franq_pag
where	nr_sequencia	= nr_seq_franq_p;

if (dt_fecha_lote_w IS NOT NULL AND dt_fecha_lote_w::text <> '') then
	ds_retorno_w := 'Fechado';
elsif (dt_lib_lote_w IS NOT NULL AND dt_lib_lote_w::text <> '') then
	ds_retorno_w := 'Liberado';
elsif (dt_gera_lote_w IS NOT NULL AND dt_gera_lote_w::text <> '') then
	ds_retorno_w := 'Gerado';
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_status_lote_franq ( nr_seq_franq_p pls_franq_pag.nr_sequencia%type) FROM PUBLIC;

