SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_prest_doc ( nr_seq_prest_tipo_doc_p pls_prestador_tipo_doc.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


dt_fim_vigencia_w	pls_prestador_tipo_doc.dt_fim_vigencia%type;
ds_retorno_w		varchar(255);
qt_aguardando_doc_w	integer;


BEGIN

select	max(dt_fim_vigencia)
into STRICT	dt_fim_vigencia_w
from	pls_prestador_tipo_doc
where	nr_sequencia 	= nr_seq_prest_tipo_doc_p;

select	count(1)
into STRICT	qt_aguardando_doc_w
from	pls_prestador_tipo_doc	a,
	pls_prestador_documento	b
where	a.nr_sequencia		= nr_seq_prest_tipo_doc_p
and	b.nr_seq_prest_tipo 	= a.nr_sequencia
and	b.ie_status 		!= 'A';	-- Aceito
if (qt_aguardando_doc_w = 0) then -- Verificar se existe registro na tabela
	select	CASE WHEN count(1)=0 THEN 1  ELSE 0 END
	into STRICT	qt_aguardando_doc_w
	from	pls_prestador_tipo_doc	a,
		pls_prestador_documento	b
	where	a.nr_sequencia		= nr_seq_prest_tipo_doc_p
	and	b.nr_seq_prest_tipo 	= a.nr_sequencia;
end if;

if (dt_fim_vigencia_w >= clock_timestamp() + interval '30 days') or (coalesce(dt_fim_vigencia_w::text, '') = '') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(1062916); -- 'Ativo';
	if (qt_aguardando_doc_w > 0) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(1062919); -- 'Aguardando documento';';
	end if;
	
elsif (dt_fim_vigencia_w < clock_timestamp()) then
	ds_retorno_w := wheb_mensagem_pck.get_texto(1062917); -- 'Vencido'; 	
	
elsif (dt_fim_vigencia_w <= clock_timestamp() + interval '30 days') then
	ds_retorno_w := wheb_mensagem_pck.get_texto(1062918); -- 'Próximo do vencimento
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
