CREATE OR REPLACE FUNCTION pls_obter_status_reinclusao ( nr_seq_rescisao_p pls_rescisao_contrato.nr_sequencia%type, nr_seq_solic_resc_benef_p pls_solic_rescisao_benef.nr_sequencia%type, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
D - descrição do status
I - ie_status
Retornar o status da reinclusão de beneficiário
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		varchar(255);


BEGIN
if (nr_seq_rescisao_p IS NOT NULL AND nr_seq_rescisao_p::text <> '') then
	if (ie_tipo_p = 'D') then
		begin
			select	substr(obter_valor_dominio(7785, ie_status),1,255) ds_status
			into STRICT	ds_retorno_w
			from	pls_reinclusao_benef
			where	nr_seq_rescisao	= nr_seq_rescisao_p;
		exception
		when others then
			ds_retorno_w	:= 'Em aberto';
		end;
	elsif (ie_tipo_p = 'I') then
		begin
			select	ie_status ie_status
			into STRICT	ds_retorno_w
			from	pls_reinclusao_benef
			where	nr_seq_rescisao	= nr_seq_rescisao_p;
		exception
		when others then
			ds_retorno_w	:= '1';
		end;
	end if;
elsif (nr_seq_solic_resc_benef_p IS NOT NULL AND nr_seq_solic_resc_benef_p::text <> '') then
	if (ie_tipo_p = 'D') then
		begin
			select	substr(obter_valor_dominio(7785, ie_status),1,255) ds_status
			into STRICT	ds_retorno_w
			from	pls_reinclusao_benef
			where	nr_seq_solic_resc_benef	= nr_seq_solic_resc_benef_p;
		exception
		when others then
			ds_retorno_w	:= 'Em aberto';
		end;
	elsif (ie_tipo_p = 'I') then
		begin
			select	ie_status ie_status
			into STRICT	ds_retorno_w
			from	pls_reinclusao_benef
			where	nr_seq_solic_resc_benef	= nr_seq_solic_resc_benef_p;
		exception
		when others then
			ds_retorno_w	:= '1';
		end;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
