SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_repasse ( nr_seq_repasse_p bigint) RETURNS varchar AS $body$
DECLARE


/*

LT - Titulo liquidado
LI- Liberado pelo us√∫ario
AB - Aberto
*/
ie_retorno_w		varchar(2);
dt_liberacao_w		timestamp;
nr_titulo_w		bigint;
qt_liquidado_w		bigint;


BEGIN

select	substr(pls_obter_titulo_mensalidade(null,nr_seq_mens_seg),1,10),
	dt_liberacao
into STRICT	nr_titulo_w,
	dt_liberacao_w
from	pls_repasse_mens
where	nr_sequencia	= nr_seq_repasse_p;

if (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') then
	ie_retorno_w	:= 'LI';
elsif (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then
	select	count(*)
	into STRICT	qt_liquidado_w
	from	titulo_receber
	where	ie_situacao	= '2'
	and	nr_titulo	= nr_titulo_w;

	if (qt_liquidado_w	> 0) then
		ie_retorno_w	:= 'LT';
	else
		ie_retorno_w	:= 'AB';
	end if;
else
	ie_retorno_w	:= 'AB';
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_status_repasse ( nr_seq_repasse_p bigint) FROM PUBLIC;

