SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_status_tit_quota (nr_titulo_p titulo_receber.nr_titulo%type) RETURNS varchar AS $body$
DECLARE


dt_pagamento_previsto_w		titulo_receber.dt_pagamento_previsto%type;
vl_saldo_titulo_w		titulo_receber.vl_saldo_titulo%type;
ie_status_w			varchar(255) := 'A';


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select	dt_pagamento_previsto,
		vl_saldo_titulo
	into STRICT	dt_pagamento_previsto_w,
		vl_saldo_titulo_w
	from	titulo_receber
	where	nr_titulo	= nr_titulo_p;

	if (vl_saldo_titulo_w = 0) then
		ie_status_w		:= 'L';
	elsif (dt_pagamento_previsto_w < trunc(clock_timestamp(), 'dd')) then
		ie_status_w		:= 'V';
	else
		ie_status_w		:= 'A';
	end if;

end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
