CREATE OR REPLACE FUNCTION pls_obter_stat_pag_item ( ie_glosa_p pls_conta_proc.ie_glosa%type, vl_liberado_p pls_conta_proc.vl_liberado%type, vl_glosa_p pls_conta_proc.vl_glosa%type) RETURNS varchar AS $body$
DECLARE


ie_status_pagamento_w 	pls_conta_proc.ie_status_pagamento%type;

BEGIN
-- por padrão é indefinido
ie_status_pagamento_w := 'I';

if (ie_glosa_p = 'S') then
	-- glosado
	ie_status_pagamento_w	:= 'G';
else
	if (vl_liberado_p > 0) and (vl_glosa_p = 0) then
		-- liberado
		ie_status_pagamento_w	:= 'L';
	else
		-- liberado parcial
		ie_status_pagamento_w	:= 'P';
	end if;
end if;

return ie_status_pagamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
