CREATE OR REPLACE FUNCTION pls_obter_tabela_edicao ( cd_estabelecimento_p bigint, nr_seq_prestador_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_vigencia_p timestamp) RETURNS varchar AS $body$
DECLARE


cd_edicao_w		integer;
cd_tabela_relat_w	varchar(20);
ds_retorno_w		varchar(20);
nr_versao_tiss_w	pls_versao_tiss.cd_versao_tiss%type;

BEGIN

/* Conforme visto com o análista Eder J da Silva, esta parte do código foi comentada, deixando somente o if que obtem a tabela do procedimento pelo código de origem.
cd_edicao_w := pls_obter_edicao_preco_proc(cd_estabelecimento_p, nr_seq_prestador_p, cd_procedimento_p, ie_origem_proced_p, dt_vigencia_p);

if	(nvl(cd_edicao_w,0) > 0) then
	select	substr(obter_descricao_padrao('TISS_TIPO_TABELA','CD_TABELA_RELAT',nr_seq_tiss_tabela),1,2)
	into	cd_tabela_relat_w
	from	edicao_amb
	where	cd_edicao_amb	= cd_edicao_w;
else*/
if (ie_origem_proced_p = 1) then
	cd_tabela_relat_w := '02'; /* AMB 92 */
elsif (ie_origem_proced_p = 5) then
	cd_tabela_relat_w := '06'; /* CBHPM */
elsif (ie_origem_proced_p = 7) then
	cd_tabela_relat_w := '10'; /* SUS_2008 */
elsif (ie_origem_proced_p = 8) then
	nr_versao_tiss_w := pls_obter_versao_tiss;

	if (coalesce(nr_versao_tiss_w::text, '') = '') then
		nr_versao_tiss_w := '2.02.03';
	end if;

	if ((substr(nr_versao_tiss_w,1,1))::numeric  >= 3) then /*Versão 3 em diante*/
		cd_tabela_relat_w := '22';
	else
		cd_tabela_relat_w := '16'; /* TUSS */
	end if;
else
	cd_tabela_relat_w := '94'; /* PROPRIO */
end if;

ds_retorno_w := cd_tabela_relat_w;

return	ds_retorno_w;

end;
$body$
