CREATE OR REPLACE FUNCTION pls_obter_tab_preco_proc_mat ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE

 
/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: Retornar o nome da tabela de preco das contas_proc e mat 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ x] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++*/ 
 
ds_retorno_w		varchar(255) 	:= '';
ie_tipo_despesa_w	varchar(255) 	:= '';
nr_seq_regra_w		bigint 	:= null;

 

BEGIN 
--obter o código da regra para pesquisar pode pesquisar a descricão 
if (nr_seq_conta_proc_p IS NOT NULL AND nr_seq_conta_proc_p::text <> '') then -- buscar na conta_proc 
 
	select	substr(max(ie_tipo_despesa), 1, 255), 
		max(nr_seq_regra) 
	into STRICT	ie_tipo_despesa_w, 
		nr_seq_regra_w 
	from	pls_conta_proc 
	where	nr_sequencia = nr_seq_conta_proc_p;
	 
	--buscar o nome da tabela dos procedimento conforme as regras abaixo 
	if 	ie_tipo_despesa_w = '1' 	then -- se um pego a descrição da pls_regra_preco_proc 
		select 	max(b.ds_edicao_amb) 
		into STRICT	ds_retorno_w 
		from	pls_regra_preco_proc a, 
			edicao_amb b 
		where	b.cd_edicao_amb = a.cd_edicao_amb 
		and	a.nr_sequencia = nr_seq_regra_w;
	elsif 	ie_tipo_despesa_w in ('2', '3') 	then -- se 2 ou 3 pego a descrição da pls_regra_preco_servico 
		select	max(b.ds_tabela_servico) 
		into STRICT	ds_retorno_w 
		from	pls_regra_preco_servico a, 
			tabela_servico b 
		where	b.cd_tabela_servico = a.cd_tabela_servico 
		and	a.nr_sequencia = nr_seq_regra_w;
	else 
		ds_retorno_w := null;
	end if;	
elsif (nr_seq_conta_mat_p IS NOT NULL AND nr_seq_conta_mat_p::text <> '') then 
	--buscar o nome da tabela dos materiais conforme as regras abaixo 
	select	max(nr_seq_regra) 
	into STRICT	nr_seq_regra_w		 
	from	pls_conta_mat 
	where	nr_sequencia = nr_seq_conta_mat_p;
	 
	--buscar o nome da tabela dos materiais 
	if (nr_seq_regra_w IS NOT NULL AND nr_seq_regra_w::text <> '') then 
		select 	max(b.ds_tabela) 
		into STRICT	ds_retorno_w 
		from	pls_material_preco b, 
			pls_regra_preco_mat a 
		where	b.nr_sequencia = nr_seq_material_preco	 
		and	a.nr_sequencia = nr_seq_regra_w;
	else 
		ds_retorno_w := null;
	end if;	
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_tab_preco_proc_mat ( nr_seq_conta_proc_p pls_conta_proc.nr_sequencia%type, nr_seq_conta_mat_p pls_conta_mat.nr_sequencia%type) FROM PUBLIC;

