CREATE OR REPLACE FUNCTION pls_obter_taxas_item ( nr_seq_taxa_p pls_taxa_item.nr_sequencia%type, ie_tipo_p text) RETURNS bigint AS $body$
DECLARE


tx_w			double precision;


BEGIN

if (ie_tipo_p = 'M') then

	select	max(tx_material)
	into STRICT	tx_w
	from	pls_taxa_item
	where	nr_sequencia	= nr_seq_taxa_p;

elsif (ie_tipo_p = 'CO') then

	select	max(tx_custo_operacional)
	into STRICT	tx_w
	from	pls_taxa_item
	where	nr_sequencia	= nr_seq_taxa_p;

elsif (ie_tipo_p = 'MED') then

	select	max(tx_medico)
	into STRICT	tx_w
	from	pls_taxa_item
	where	nr_sequencia	= nr_seq_taxa_p;

end if;

return	coalesce(tx_w,100);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
