CREATE OR REPLACE FUNCTION pls_obter_taxa_contrato (nr_seq_intercambio_p bigint, dt_mesano_referencia_p timestamp, nr_seq_contrato_p bigint, nr_seq_plano_p bigint) RETURNS bigint AS $body$
DECLARE


tx_administracao_w	pls_regra_pos_estabelecido.tx_administracao%type;
vl_informado_w		pls_regra_pos_estabelecido.vl_informado%type;

C01 CURSOR FOR
	SELECT	tx_administracao
	from	pls_regra_pos_estabelecido	a,
		pls_contrato			b
	where	b.nr_sequencia	= a.nr_seq_contrato
	and	b.nr_sequencia	= nr_seq_contrato_p
	and	dt_mesano_referencia_p between coalesce(dt_vigencia_inicio,dt_mesano_referencia_p) and
								fim_dia(coalesce(dt_vigencia_fim,dt_mesano_referencia_p))
	and (a.ie_cobranca	= 'A' or coalesce(a.ie_cobranca::text, '') = '')
	
union all

	SELECT	tx_administracao
	from	pls_regra_pos_estabelecido	a,
		pls_intercambio			b
	where	b.nr_sequencia	= a.nr_seq_intercambio
	and	b.nr_sequencia	= nr_seq_intercambio_p
	and	dt_mesano_referencia_p between coalesce(dt_vigencia_inicio,dt_mesano_referencia_p) and
								fim_dia(coalesce(dt_vigencia_fim,dt_mesano_referencia_p))
	and (a.ie_cobranca	= 'A' or coalesce(a.ie_cobranca::text, '') = '')
	
union

	select	tx_administracao
	from	pls_regra_pos_estabelecido	a
	where	a.nr_seq_plano	= nr_seq_plano_p
	and	dt_mesano_referencia_p between coalesce(dt_vigencia_inicio,dt_mesano_referencia_p) and
								fim_dia(coalesce(dt_vigencia_fim,dt_mesano_referencia_p))
	and	not exists	(select	1
				from	pls_regra_pos_estabelecido x
				where	((x.nr_seq_contrato	= nr_seq_contrato_p) or (x.nr_seq_intercambio = nr_seq_intercambio_p)))
	and (a.ie_cobranca	= 'A' or coalesce(a.ie_cobranca::text, '') = '');


BEGIN

open C01;
loop
fetch C01 into
	tx_administracao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	null;
	end;
end loop;
close C01;

return	tx_administracao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
