SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_taxa_inscricao_plano ( nr_seq_simul_individual_p bigint, nr_seq_simul_coletivo_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_plano_w			bigint;
ie_grau_dependencia_w		varchar(2);
nr_seq_regra_w			bigint;
vl_inscricao_w			double precision;
tx_inscricao_w			double precision;
vl_mensalidade_w		double precision;
ds_retorno_w			double precision;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.vl_inscricao,
		a.tx_inscricao
	from	pls_regra_inscricao a
	where	a.nr_seq_plano		= nr_seq_plano_w
	and	(((coalesce(a.ie_grau_dependencia,ie_grau_dependencia_w) = ie_grau_dependencia_w) or (a.ie_grau_dependencia = 'A')) or (ie_grau_dependencia_w = 'A'))
	and	trunc(clock_timestamp(),'month') between trunc(coalesce(dt_inicio_vigencia,clock_timestamp()),'month') and trunc(coalesce(dt_fim_vigencia,clock_timestamp()),'month');


BEGIN

if (nr_seq_simul_individual_p IS NOT NULL AND nr_seq_simul_individual_p::text <> '') then
	select	ie_tipo_benef,
		nr_seq_produto,
		vl_mensalidade
	into STRICT	ie_grau_dependencia_w,
		nr_seq_plano_w,
		vl_mensalidade_w
	from	pls_simulpreco_individual
	where	nr_sequencia	= nr_seq_simul_individual_p;
elsif (nr_seq_simul_coletivo_p IS NOT NULL AND nr_seq_simul_coletivo_p::text <> '') then
	select	'A',
		nr_seq_plano,
		vl_mensalidade
	into STRICT	ie_grau_dependencia_w,
		nr_seq_plano_w,
		vl_mensalidade_w
	from	pls_simulpreco_coletivo
	where	nr_sequencia	= nr_seq_simul_coletivo_p;
end if;

open c01;
loop
fetch c01 into
	nr_seq_regra_w,
	vl_inscricao_w,
	tx_inscricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_retorno_w	:= coalesce(ds_retorno_w,0) + ((coalesce(tx_inscricao_w,0) / 100) * coalesce(vl_mensalidade_w,0)) + coalesce(vl_inscricao_w,0);
	end;
end loop;
close c01;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
