CREATE OR REPLACE FUNCTION pls_obter_taxa_interc_conta ( dt_conta_p timestamp, dt_prev_envio_p timestamp, nr_seq_congenere_p bigint, nr_seq_segurado_p bigint) RETURNS bigint AS $body$
DECLARE


pr_taxa_w			double precision;
nr_seq_intercambio_w		bigint;
cd_estabelecimento_w		smallint;
ie_taxa_w			varchar(10);
qt_dia_proced_receb_w		bigint;


BEGIN

begin
select	max(nr_seq_intercambio),
	max(cd_estabelecimento)
into STRICT	nr_seq_intercambio_w,
	cd_estabelecimento_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;
exception
when others then
	nr_seq_intercambio_w	:= null;
end;


qt_dia_proced_receb_w	:= (trunc(dt_prev_envio_p) - trunc(dt_conta_p));


SELECT * FROM pls_obter_taxa_operadora('P', qt_dia_proced_receb_w, nr_seq_congenere_p, cd_estabelecimento_w, nr_seq_segurado_p, dt_conta_p, '', null, null, pr_taxa_w, ie_taxa_w) INTO STRICT pr_taxa_w, ie_taxa_w;

return pr_taxa_w;

end;
$body$
LANGUAGE PLPGSQL
