CREATE OR REPLACE FUNCTION pls_obter_taxa_reajuste ( nr_seq_reajuste_p pls_reajuste.nr_sequencia%type, nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_lote_referencia_p pls_reajuste.nr_seq_lote_referencia%type, tx_reajuste_p pls_reajuste.tx_reajuste%type) RETURNS bigint AS $body$
DECLARE


tx_reajuste_w		pls_reajuste.tx_reajuste%type;
nr_seq_reajuste_w	pls_reajuste.nr_sequencia%type;


BEGIN

tx_reajuste_w	:= 0;

if (coalesce(nr_seq_lote_referencia_p::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_reajuste_w
	from	pls_reajuste
	where	nr_seq_lote_referencia = nr_seq_reajuste_p
	and	nr_seq_contrato = nr_seq_contrato_p;

	if (nr_seq_reajuste_w IS NOT NULL AND nr_seq_reajuste_w::text <> '') then
		select	tx_reajuste
		into STRICT	tx_reajuste_w
		from	pls_reajuste
		where	nr_sequencia = nr_seq_reajuste_w;
	else
		tx_reajuste_w	:= tx_reajuste_p;
	end if;
else
	tx_reajuste_w	:= tx_reajuste_p;
end if;

return	tx_reajuste_w;

end;
$body$
