SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION pls_obter_tel_end_comerci_adic ( nr_seq_requisicao_p bigint, nr_seq_prestador_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_seq_compl_pf_tel_adic_w	bigint;
nr_seq_compl_pj_w		bigint;
ds_retorno_w			varchar(4000);


BEGIN 
 
begin 
	select	max(nr_telef_secretaria) 
	into STRICT	ds_retorno_w 
	from	pls_auditoria 
	where	nr_seq_requisicao	= nr_seq_requisicao_p;
exception 
when others then 
	ds_retorno_w	:= 'X';
end;
 
if (coalesce(ds_retorno_w,'X')	= 'X') then 
	begin 
		select	nr_seq_compl_pf_tel_adic, 
			nr_seq_compl_pj 
		into STRICT	nr_seq_compl_pf_tel_adic_w, 
			nr_seq_compl_pj_w 
		from	pls_prestador 
		where	nr_sequencia	= nr_seq_prestador_p;
	exception 
	when others then 
		nr_seq_compl_pf_tel_adic_w	:= 0;
		nr_seq_compl_pj_w		:= 0;
	end;
 
	if (coalesce(nr_seq_compl_pf_tel_adic_w,0)	<> 0) then 
		begin 
			select	'('||nr_ddd_telefone||')'||nr_telefone 
			into STRICT	ds_retorno_w 
			from	compl_pf_tel_adic 
			where	nr_sequencia	= nr_seq_compl_pf_tel_adic_w;
		exception 
		when others then 
			ds_retorno_w	:= '';
		end;
	elsif (coalesce(nr_seq_compl_pj_w,0)		<> 0) then 
		begin 
			select	'('||nr_ddd_telefone||')'||nr_telefone 
			into STRICT	ds_retorno_w 
			from	pessoa_juridica_compl 
			where	nr_sequencia	= nr_seq_compl_pj_w;
		exception 
		when others then 
			ds_retorno_w	:= '';
		end;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
