CREATE OR REPLACE FUNCTION pls_obter_tempo_acesso_analise ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, ie_tipo_acesso_p text) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_tempo_auditor_w		double precision;
qt_tempo_total_w		double precision	:= 0;

C01 CURSOR FOR
	SELECT	trunc((coalesce(b.dt_fim_acesso,clock_timestamp()) - b.dt_inicio_acesso) * 1440,2) qt_tempo
	from	pls_analise_log_acesso		b
	where	b.nr_seq_analise	= nr_seq_analise_p
	and	((b.nr_seq_grupo_auditor	= nr_seq_grupo_p) or (coalesce(nr_seq_grupo_p::text, '') = ''))
	and	b.ie_tipo_acesso	= ie_tipo_acesso_p;


BEGIN
open C01;
loop
fetch C01 into
	qt_tempo_auditor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_tempo_total_w	:= qt_tempo_total_w + qt_tempo_auditor_w;
	end;
end loop;
close C01;

return qt_tempo_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION pls_obter_tempo_acesso_analise ( nr_seq_analise_p bigint, nr_seq_grupo_p bigint, ie_tipo_acesso_p text) FROM PUBLIC;

