CREATE OR REPLACE FUNCTION pls_obter_tempo_analise_total ( nr_seq_analise_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);


BEGIN

select	formatar_hora(null, sum(coalesce(a.dt_final_analise,clock_timestamp()) - a.dt_inicio_analise))
into STRICT	ds_retorno_w
from	pls_tempo_conta_grupo a,
	pls_auditoria_conta_grupo b
where	b.nr_sequencia		= a.nr_seq_auditoria
and	b.nr_seq_analise 	= nr_seq_analise_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
