CREATE OR REPLACE FUNCTION pls_obter_tempo_benef_contrib ( nr_seq_segurado_p bigint, nr_seq_alteracao_motivo_p bigint, dt_inicio_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_adesao_w		timestamp;
qt_meses_incluido_w	integer;
dt_admissao_w		timestamp;
ie_tipo_data_w		varchar(10);
dt_contratacao_w	timestamp;
dt_contribuicao_w	timestamp;
dt_calculo_base_w	timestamp;
qt_meses_contrib_w	integer;
nr_seq_solicitacao_w	pls_solicitacao_rescisao.nr_sequencia%type;


BEGIN

select	max(a.nr_sequencia)
into STRICT	nr_seq_solicitacao_w
from	pls_solicitacao_rescisao a,
	pls_solic_rescisao_benef b
where	a.nr_sequencia = b.nr_seq_solicitacao
and	b.nr_seq_segurado = nr_seq_segurado_p
and	a.ie_status = 3;

if (nr_seq_solicitacao_w IS NOT NULL AND nr_seq_solicitacao_w::text <> '') then
	select  max(qt_meses_contribuicao)
	into STRICT 	qt_meses_contrib_w
	from	pls_solic_rescisao_benef
	where 	nr_seq_segurado	= nr_seq_segurado_p
	and	nr_seq_solicitacao = nr_seq_solicitacao_w;
end if;

if (coalesce(qt_meses_contrib_w::text, '') = '') then
	select	dt_inclusao_operadora,
		dt_contratacao
	into STRICT	dt_adesao_w,
		dt_contratacao_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;

	select	max(dt_admissao),
		max(dt_contribuicao)
	into STRICT	dt_admissao_w,
		dt_contribuicao_w
	from	pls_segurado_compl
	where	nr_seq_segurado	= nr_seq_segurado_p;

	begin
	select	coalesce(ie_tipo_data,'I')
	into STRICT	ie_tipo_data_w
	from	pls_motivo_alt_pagador
	where	nr_sequencia	= nr_seq_alteracao_motivo_p
	and	ie_situacao	= 'A';
	exception
	when others then
		ie_tipo_data_w		:= 'I';
	end;

	if (ie_tipo_data_w	= 'I') then
		dt_calculo_base_w	:= dt_adesao_w;
	elsif (ie_tipo_data_w	= 'A') then
		dt_calculo_base_w	:= dt_admissao_w;
	elsif (ie_tipo_data_w	= 'C') then
		dt_calculo_base_w	:= dt_contratacao_w;
	elsif (ie_tipo_data_w	= 'T') then
		dt_calculo_base_w	:= coalesce(dt_contribuicao_w,dt_adesao_w);
	end if;

	select	round(months_between(coalesce(dt_inicio_p,clock_timestamp()), dt_calculo_base_w)) + 1
	into STRICT	qt_meses_incluido_w
	;
else
	qt_meses_incluido_w	:= qt_meses_contrib_w;
end if;

return	qt_meses_incluido_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
