CREATE OR REPLACE FUNCTION pls_obter_tempo_lib_auditor ( nr_seq_grupo_auditor_p bigint, nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


nr_retorno_w			varchar(10);
dt_liberacao_ant_w		timestamp;
qt_grupo_w			smallint;
qt_grupo_ant_w			smallint;
nr_seq_ordem_w			bigint;
ie_status_w			varchar(2);
dt_liberacao_w			timestamp;
dt_atualizacao_nrec_w		timestamp;


BEGIN

select	nr_seq_ordem,
	ie_status,
	dt_liberacao,
	dt_atualizacao_nrec
into STRICT	nr_seq_ordem_w,
	ie_status_w,
	dt_liberacao_w,
	dt_atualizacao_nrec_w
from	pls_auditoria_grupo
where	nr_sequencia = nr_seq_grupo_auditor_p;

select	count(*)
into STRICT	qt_grupo_w
from	pls_auditoria_grupo
where	nr_seq_ordem < nr_seq_ordem_w
and	coalesce(dt_liberacao::text, '') = ''
and	nr_seq_auditoria = nr_seq_auditoria_p;
-- Quantidade de grupos de ordem menor sem
select	max(dt_liberacao)
into STRICT	dt_liberacao_ant_w
from	pls_auditoria_grupo
where	nr_seq_ordem < nr_seq_ordem_w
and	nr_seq_auditoria = nr_seq_auditoria_p;

if (qt_grupo_w = 0) then
	if (coalesce(dt_liberacao_ant_w::text, '') = '') then
		dt_liberacao_ant_w := dt_atualizacao_nrec_w;
	end if;
	if (ie_status_w = 'P')  then
		nr_retorno_w := substr(obter_horas_minutos_segundos((coalesce(dt_liberacao_w, clock_timestamp()) - dt_liberacao_ant_w) * 86400),1,10);
	else
		nr_retorno_w := substr(obter_horas_minutos_segundos((clock_timestamp() - dt_liberacao_ant_w) * 86400),1,10);
	end if;
else
	nr_retorno_w := '00:00:00';
end if;

return	nr_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
