CREATE OR REPLACE FUNCTION pls_obter_tempo_vida_benef ( nr_seq_segurado_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_nasc_benef_w			timestamp;
qt_retorno_w			varchar(255)				;


BEGIN

dt_nasc_benef_w	:= to_date(pls_obter_dados_segurado(nr_seq_segurado_p,'NAS'),'dd/mm/yyyy');

if (ie_opcao_p	= 'A') then
	qt_retorno_w	:= Obter_Idade(dt_nasc_benef_w, clock_timestamp(), 'A');
elsif (ie_opcao_p	= 'M') then
	qt_retorno_w	:= Obter_Idade(dt_nasc_benef_w, clock_timestamp(), 'MM');
elsif (ie_opcao_p	= 'D') then
	qt_retorno_w	:= Obter_Idade(dt_nasc_benef_w, clock_timestamp(), 'DI');
end if;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
