CREATE OR REPLACE FUNCTION pls_obter_tipo_analise_cab_aut (nr_seq_auditoria_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_guia_w		bigint	:= 0;
nr_seq_requisicao_w	bigint	:= 0;
nr_seq_execucao_w	bigint	:= 0;


BEGIN

begin
	select	nr_seq_guia,
		nr_seq_requisicao,
		nr_seq_execucao
	into STRICT	nr_seq_guia_w,
		nr_seq_requisicao_w,
		nr_seq_execucao_w
	from	pls_auditoria
	where	nr_sequencia	= nr_seq_auditoria_p;
exception
when others then
	nr_seq_guia_w		:= 0;
	nr_seq_requisicao_w	:= 0;
	nr_seq_execucao_w	:= 0;
end;

if (coalesce(nr_seq_guia_w,0)		<> 0) then
	ds_retorno_w	:= 'Guia nº '||nr_seq_guia_w;
elsif (coalesce(nr_seq_requisicao_w,0)	<> 0) then
	ds_retorno_w	:= 'Requisição nº '||nr_seq_requisicao_w;
elsif (coalesce(nr_seq_execucao_w,0)	<> 0) then
	ds_retorno_w	:= 'Execução nº '||nr_seq_execucao_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
