CREATE OR REPLACE FUNCTION pls_obter_tipo_anexo_req ( nr_seq_requisicao_p pls_requisicao.nr_sequencia%type, nr_seq_req_proc_p pls_requisicao_proc.nr_sequencia%type, nr_seq_req_mat_p pls_requisicao_mat.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Retornar o tipo de anexo da requisição
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  x ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		pls_lote_anexo_guias_aut.ie_tipo_anexo%type;
cd_versao_tiss_w	pls_versao_tiss.cd_versao_tiss%type;


BEGIN
cd_versao_tiss_w	:= pls_obter_versao_tiss;

if (nr_seq_req_proc_p IS NOT NULL AND nr_seq_req_proc_p::text <> '') then
	if (cd_versao_tiss_w >= '3.03.00') then
		begin
			select	ie_tipo_anexo
			into STRICT	ds_retorno_w
			from	pls_requisicao_proc
			where	nr_sequencia		= nr_seq_req_proc_p
			and	ie_tipo_anexo		= 'RA'
			and	coalesce(nr_seq_motivo_exc::text, '') = '';
		exception
		when others then
			ds_retorno_w	:= null;
		end;
	else
		begin
			select	y.ie_tipo_anexo
			into STRICT	ds_retorno_w
			from	pls_lote_anexo_proc_aut x,
				pls_lote_anexo_guias_aut y
			where 	x.nr_seq_lote_anexo_guia	= y.nr_sequencia
			and 	x.nr_seq_req_proc 		= nr_seq_req_proc_p
			and	coalesce(y.nr_seq_motivo_exc::text, '') = '';
		exception
		when others then
			ds_retorno_w	:= null;
		end;
	end if;
elsif (nr_seq_req_mat_p IS NOT NULL AND nr_seq_req_mat_p::text <> '') then
	begin
		select	y.ie_tipo_anexo
		into STRICT	ds_retorno_w
		from 	pls_lote_anexo_mat_aut x,
			pls_lote_anexo_guias_aut y
		where 	x.nr_seq_lote_anexo_guia 	= y.nr_sequencia
		and 	x.nr_seq_req_mat 		= nr_seq_req_mat_p
		and	coalesce(y.nr_seq_motivo_exc::text, '') = '';
	exception
	when others then
		ds_retorno_w	:= null;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
