CREATE OR REPLACE FUNCTION pls_obter_tipo_campo_reg_senha ( nr_seq_regra_gera_senha_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter a descrição do tipo de campo utilizado na regra
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [ ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:Performance.
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(255);
ie_tipo_campo_w			varchar(1);


BEGIN

begin
	select	ie_tipo_campo
	into STRICT	ie_tipo_campo_w
	from	pls_aut_gera_senha_campo
	where	nr_seq_regra_gera_senha	= nr_seq_regra_gera_senha_p;
exception
when others then
	ie_tipo_campo_w	:= null;
end;

if (ie_tipo_campo_w IS NOT NULL AND ie_tipo_campo_w::text <> '') then
	ds_retorno_w	:= substr(obter_valor_dominio(5577, ie_tipo_campo_w),1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
