CREATE OR REPLACE FUNCTION pls_obter_tipo_contrato_rel ( nr_seq_segurado_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nr_seq_contrato_w		bigint;
nr_seq_intercambio_w		bigint;
nr_seq_congenere_w		bigint;


BEGIN

select	coalesce(nr_seq_contrato,0),
	coalesce(nr_seq_intercambio,0),
	coalesce(nr_seq_congenere,0)
into STRICT	nr_seq_contrato_w,
	nr_seq_intercambio_w,
	nr_seq_congenere_w
from	pls_segurado
where	nr_sequencia	= nr_seq_segurado_p;

if (nr_seq_contrato_w	<> 0) then
	select	substr(obter_valor_dominio(3371,ie_tipo_operacao),1,255)
	into STRICT	ds_retorno_w
	from	pls_contrato
	where	nr_sequencia	= nr_seq_contrato_w;
elsif (nr_seq_intercambio_w	<> 0) then
	select	substr(obter_valor_dominio(3769,ie_tipo_contrato),1,255)
	into STRICT	ds_retorno_w
	from	pls_intercambio
	where	nr_sequencia	= nr_seq_intercambio_w;
elsif (nr_seq_congenere_w	<> 0) then
	ds_retorno_w	:= pls_obter_nome_congenere(nr_seq_congenere_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
