CREATE OR REPLACE FUNCTION pls_obter_tipo_exp_fatura ( nr_sequencia_p pls_fatura.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ie_tipo_exportacao_w	ptu_fatura.ie_tipo_exportacao%type;


BEGIN

select coalesce(max(pc.ie_tipo_exportacao),'TXT')
into STRICT 	ie_tipo_exportacao_w
from 	pls_fatura pf,
	pls_congenere pc
where 	lpad(pc.cd_cooperativa,4,'0') 	= lpad(trim(both pls_obter_dados_cooperativa(pf.nr_seq_congenere,'C')),4,0)  
and 	pf.nr_sequencia 		= nr_sequencia_p;

return ie_tipo_exportacao_w;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
