CREATE OR REPLACE FUNCTION pls_obter_tipo_guia_conta ( nr_seq_guia_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_guia_w	varchar(2);


BEGIN
if (nr_seq_guia_p IS NOT NULL AND nr_seq_guia_p::text <> '') then
	begin
	select	CASE WHEN ie_tipo_guia=2 THEN 4 WHEN ie_tipo_guia=3 THEN 3  ELSE 5 END
	into STRICT	ie_tipo_guia_w
	from	pls_guia_plano
	where	nr_sequencia = nr_seq_guia_p;
	end;
end if;
return	ie_tipo_guia_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
