CREATE OR REPLACE FUNCTION pls_obter_tipo_guia_principal ( nr_seq_analise_p bigint) RETURNS varchar AS $body$
DECLARE


ie_tipo_guia_w			varchar(2);
qt_guias_w			bigint;
ie_retorno_w			varchar(2);

/*askono-OS 338737 - Obter tipo de guia principal da anÃ¡lise*/

BEGIN
ie_tipo_guia_w	:= '';

begin
select 	1
into STRICT	qt_guias_w
from   	pls_analise_conta a,
	pls_conta b
where  	b.nr_seq_analise = a.nr_sequencia
and	b.ie_tipo_guia = '5'
and    	a.nr_sequencia =  nr_seq_analise_p;
exception
when others then
	qt_guias_w := 0;
end;

if (qt_guias_w > 0) then
	ie_tipo_guia_w	:= '5';
else
	begin
	select 	1
	into STRICT	qt_guias_w
	from   	pls_analise_conta a,
		pls_conta b
	where  	b.nr_seq_analise = a.nr_sequencia
	and	b.ie_tipo_guia = '3'
	and    	b.nr_seq_analise =  nr_seq_analise_p;
	exception
	when others then
		qt_guias_w := 0;
	end;

	if (qt_guias_w > 0) then
		ie_tipo_guia_w	:= '3';
	else
		begin
		select 	1
		into STRICT	qt_guias_w
		from   	pls_analise_conta a,
			pls_conta b
		where  	b.nr_seq_analise = a.nr_sequencia
		and	b.ie_tipo_guia = '4'
		and    	b.nr_seq_analise =  nr_seq_analise_p;
		exception
		when others then
			qt_guias_w := 0;
		end;

		if (qt_guias_w > 0) then
			ie_tipo_guia_w	:= '4';
		end if;
	end if;
end if;

ie_retorno_w	:= ie_tipo_guia_w;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
