CREATE OR REPLACE FUNCTION pls_obter_tipo_intercambio ( nr_seq_congenere_p pls_congenere.nr_sequencia%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) RETURNS PLS_REGRA_INTERCAMBIO.IE_TIPO_INTERCAMBIO%TYPE AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:	Retorna o tipo de intercâmbio que foi realizado. Verifica o estado da operadora congenere, se
	for igual o estado da outorgante então é intercâmbio estadual, caso contrário é intercâmbio
	nacional.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[X] Objetos do dicionário [ X] Tasy (Delphi/Java) [] Portal []  Relatórios [] Outros:
------------------------------------------------------------------------------------------------------------------
Pontos de atenção:

Alterações:
------------------------------------------------------------------------------------------------------------------
jjung OS 606930 - 20/08/2013  Criação da function.
------------------------------------------------------------------------------------------------------------------
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
sg_uf_outorgante_w	pessoa_juridica.sg_estado%type;
sg_uf_congenere_w	pessoa_juridica.sg_estado%type;

ie_tipo_interambio_w	pls_regra_intercambio.ie_tipo_intercambio%type;
ie_nacional_w		pls_congenere.ie_nacional%type;


BEGIN
-- Começa como sendo nada.
ie_tipo_interambio_w := null;

-- Se não tiver uma destas informações então não tem como saber qual o tipo de intercâmbio, portanto retorna nada.
if (nr_seq_congenere_p IS NOT NULL AND nr_seq_congenere_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then

	-- Obter o estado cadastrado para a outorgante.
	select	max(b.sg_estado)
	into STRICT	sg_uf_outorgante_w
	from	pls_outorgante		a,
		pessoa_juridica		b
	where	a.cd_estabelecimento	= cd_estabelecimento_p
	and	b.cd_cgc		= a.cd_cgc_outorgante;

	-- Obter o estado cadastrado para a congenere.
	select	max(b.sg_estado),
		coalesce(max(ie_nacional), 'N')
	into STRICT	sg_uf_congenere_w,
		ie_nacional_w
	from	pls_congenere	a,
		pessoa_juridica	b
	where	a.nr_sequencia		= nr_seq_congenere_p
	and	b.cd_cgc		= a.cd_cgc;

	if (ie_nacional_w	= 'S') then
		-- Nacional
		ie_tipo_interambio_w := 'N';
	elsif (sg_uf_outorgante_w IS NOT NULL AND sg_uf_outorgante_w::text <> '') and -- Se tiver as duas informações de estado da congenere ou da outorgante então verifica se são iguais ou diferente para saber qual o tipo de intercâmbio.
		(sg_uf_congenere_w IS NOT NULL AND sg_uf_congenere_w::text <> '') then

		-- Se forem iguais  é estadual se não é nacional.
		if (sg_uf_outorgante_w = sg_uf_congenere_w) then
			-- Estadual
			ie_tipo_interambio_w := 'E';
		else
			-- Nacional
			ie_tipo_interambio_w := 'N';
		end if;
	-- Se não tiver alguma informação de estado da congenere ou da outorgante então não retorna nada.
	else
		ie_tipo_interambio_w := null;
	end if;
end if;

return ie_tipo_interambio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
